dsl.codeExample:
present quiz "my-quiz" {
  rules {
    rule content "questions" {
      for question in quiz.questions {
        slide {
          content dynamic {
            name: "question-slide"
            attrs { question: question.text }
          }
        }
      }
    }
  }
}

