modular.codeExample:
import { SlideEngine } from '@slidejs/core';
import { parseSlideDSL, compile } from '@slidejs/dsl';

const ast = await parseSlideDSL(dslSource);
const slideDSL = compile(ast);
const engine = new SlideEngine(slideDSL);
